import React, { useState, useEffect, Component } from "react";
import { connect } from "react-redux";
import { getQuestionById, voteQuestion } from "../redux/index";
import  Link  from "next/link";
import { List, Icon, Avatar, Button, Tag } from "antd";
import Replies from "../components/questionDescription/Replies";
import Reply from "../components/reply/Reply";
import ReactMarkdown from "react-markdown";
import Router from 'next/router'
const IconText = ({ type, text }) => (
  <span>
    <Icon type={type} style={{ marginRight: 8 }} />
    {text}
  </span>
);

const getUpVotesLength = votes => {
  const upVotes = votes.filter(({ type }) => type === true);
  return upVotes.length;
};

const getDownVotesLength = votes => {
  const upVotes = votes.filter(({ type }) => type === false);
  return upVotes.length;
};
const renderTags = (tags) => {
  return tags.map(tag =>  <Tag color="blue">{tag}</Tag>)
}
class QuestionDescription extends Component {
  // useEffect(() => {
  //   props.getQuestionById(props.match.params.id);
  // }, []);
  // componentDidMount = () => {
  //   this.props.getQuestionById(Router.params.id);
  // }
  static async getInitialProps({ store, isServer, pathname, query }) {
    try {
      console.log(pathname)
      const action = await getQuestionById(Router.params.id);

      await action(store.dispatch);

      return { currentQuestion: store.getState().currentQuestion };
    } catch (err) {
      return {};
    }
  }
  render = () =>{
  const props = this.props
  console.log(props.currentQuestion);
  return (
    <List
      itemLayout="vertical"
      size="large"
      style={{ padding: "0 10%" }}
      dataSource={props.currentQuestion._id ? [props.currentQuestion] : []}
      loading={props.currentQuestionLoader}
      renderItem={item => (
        <div>
          <List.Item
            key={item._id}
            actions={[
              <Button    onClick={() => props.voteQuestion(item._id, true, props.history)}>

                <IconText
                  type="up"

                  text={getUpVotesLength(item.votes)}
                  key="list-vertical-star-o"
                />
              </Button>,
              <Button   onClick={() => props.voteQuestion(item._id, false,props.history)}>
                <IconText
                  type="down"

                  text={getDownVotesLength(item.votes)}
                  key="list-vertical-like-o"
                />
              </Button>,
              <Button>
                <IconText
                  type="message"
                  text={item.replies.length}
                  key="list-vertical-message"
                />
              </Button>,
              <Reply questionId={item._id} {...props}>Add Reply</Reply>
            ]}
          >
            <List.Item.Meta
              avatar={
                <Avatar style={{
                  backgroundColor: "#f56a00",
                  verticalAlign: "middle"
                }}
                size="large" >{item.creator.displayName[0].toUpperCase()} </Avatar>
              }
              title={<a href={item.href}>{item.title}</a>}
              description={renderTags(item.tags)}
            />
            <ReactMarkdown source={item.description}/>
          </List.Item>
          <Replies replies={item.replies} {...props} />


        </div>
      )}
    />

  );
}
}
const mapStateToProps = state => ({
  currentQuestion: state.question.currentQuestion,
  currentQuestionLoader: state.question.currentQuestionLoader
});

const mapDispatchToProps = dispatch => {
  return {
    getQuestionById: id => getQuestionById(id)(dispatch),
    voteQuestion: (id, type, history) => voteQuestion(id, type, history)(dispatch)
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(QuestionDescription);
