import React, { useState, useEffect, Component } from "react";
import { connect } from "react-redux";
import { getQuestionById, voteQuestion } from "../redux/index";
import Link from "next/link";
import { List, Icon, Avatar, Button, Tag } from "antd";
import Replies from "../components/questionDescription/Replies";
import Reply from "../components/reply/Reply";
import ReactMarkdown from "react-markdown";
import Layout from "../components/Layout";
import Router from "next/router";
const IconText = ({ type, text }) => (
  <span>
    <Icon type={type} style={{ marginRight: 8 }} />
    {text}
  </span>
);

const getUpVotesLength = votes => {
  const upVotes = votes.filter(({ type }) => type === true);
  return upVotes.length;
};

const getDownVotesLength = votes => {
  const upVotes = votes.filter(({ type }) => type === false);
  return upVotes.length;
};
const renderTags = tags => {
  return tags.map(tag => <Tag color="blue">{tag}</Tag>);
};
class QuestionDescription extends Component {

  static async getInitialProps({ store, isServer, pathname, query }) {
    try {
      const action = await getQuestionById(query.id);
      await action(store.dispatch);
      return { currentQuestion: store.getState().question.currentQuestion };
    } catch (err) {
      return {};
    }
  }
  render = () => {
    const props = this.props;
    console.log(props.currentQuestion);
    return (
      <Layout>
        <List
          itemLayout="vertical"
          size="large"
          style={{ padding: "0 10%" }}
          dataSource={props.currentQuestion._id ? [props.currentQuestion] : []}
          loading={props.currentQuestionLoader}
          renderItem={item => (
            <QuestionDescription item={item} {...props}/>
          )}
        />
      </Layout>
    );
  };
}
const mapStateToProps = state => ({
  currentQuestion: state.question.currentQuestion,
  currentQuestionLoader: state.question.currentQuestionLoader
});

const mapDispatchToProps = dispatch => {
  return {
    getQuestionById: id => getQuestionById(id)(dispatch),
    voteQuestion: (id, type) =>
      voteQuestion(id, type)(dispatch)
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(QuestionDescription);
