import React from 'react';
import { List, Skeleton, Avatar, Tag } from "antd";

import Link from "next/link";

const colorList = ["#f56a00", "#7265e6", "#ffbf00", "#00a2ae"];

const getUpVotesLength = votes => {
  const upVotes = votes.filter(({ type }) => type === true);
  return upVotes.length;
};

const getDownVotesLength = votes => {
  const upVotes = votes.filter(({ type }) => type === false);
  return upVotes.length;
};

const renderTags = (tags,keys) => {
  return tags.map((tag,index) => <Tag key={index} color="blue">{tag}</Tag>);
};
function Questions(props) {
    const {item,index} = props
    return (
        <List.Item

        actions={[
          <a key="list-loadmore-more-1">
            {getUpVotesLength(item.votes)} Up Votes
          </a>,
          <a key="list-loadmore-more">
            {getDownVotesLength(item.votes)} Down Votes
          </a>
        ]}
      >
        <Skeleton
          avatar
          title={false}
          loading={props.questionsLoader}
          active
        >
          <List.Item.Meta
            avatar={
              <Avatar
                style={{
                  backgroundColor: colorList[index % 4],
                  verticalAlign: "middle"
                }}
                size="large"
              >
                {item.creator.displayName[0].toUpperCase()}
              </Avatar>
            }
            title={
              <Link href={`/question/${item._id}`}>
                <a
                  style={{
                    fontSize: "1.4rem",
                    margin: "0 0 0.5rem 0",
                    display: "inline-block"
                  }}
                >
                  {item.title}
                </a>
              </Link>
            }
            description={renderTags(item.tags,index)}
          />
          <div>{item.creator.displayName}</div>
        </Skeleton>
      </List.Item>

    );
}

export default Questions;
