import React, { useEffect, Component } from "react";
import { List, Button, Skeleton, Avatar, Tag } from "antd";
import { connect } from "react-redux";
import { getQuestions } from "../redux/index";
import Layout from "../components/Layout";
import Link from "next/link";
import AskQuestion from "../components/askQuestion/AskQuestion";
const colorList = ["#f56a00", "#7265e6", "#ffbf00", "#00a2ae"];

const getUpVotesLength = votes => {
  const upVotes = votes.filter(({ type }) => type === true);
  return upVotes.length;
};

const getDownVotesLength = votes => {
  const upVotes = votes.filter(({ type }) => type === false);
  return upVotes.length;
};

const renderTags = tags => {
  return tags.map(tag => <Tag color="blue">{tag}</Tag>);
};
class Questions extends Component {
  static async getInitialProps({ store, isServer, pathname, query }) {
    try {
      const action = await getQuestions("");

      await action(store.dispatch);

      return { questions: store.getState().questions };
    } catch (err) {
      return {};
    }
  }
  render() {
    const props = this.props;
    console.log(props);
    return (
      <Layout>
        <div style={{ padding: "0 10%" }}>
          <List

            className="demo-loadmore-list"
            loading={props.questionsLoader}
            itemLayout="horizontal"
            dataSource={props.questions}
            renderItem={(item, index) => (
              <List.Item
              key={index}
                actions={[
                  <a key="list-loadmore-more-1">
                    {getUpVotesLength(item.votes)} Up Votes
                  </a>,
                  <a key="list-loadmore-more">
                    {getDownVotesLength(item.votes)} Down Votes
                  </a>
                ]}
              >
                <Skeleton
                  avatar
                  title={false}
                  loading={props.questionsLoader}
                  active
                >
                  <List.Item.Meta
                    avatar={
                      <Avatar
                        style={{
                          backgroundColor: colorList[index % 4],
                          verticalAlign: "middle"
                        }}
                        size="large"
                      >
                        {item.creator.displayName[0].toUpperCase()}
                      </Avatar>
                    }
                    title={
                      <Link href={`/${item._id}`}>
                        <a
                          style={{
                            fontSize: "1.4rem",
                            margin: "0 0 0.5rem 0",
                            display: "inline-block"
                          }}
                        >
                          {item.title}
                        </a>
                      </Link>
                    }
                    description={renderTags(item.tags)}
                  />
                  <div>{item.creator.displayName}</div>
                </Skeleton>
              </List.Item>
            )}
          />
          <div style={{ padding: "2rem" }}>
            <AskQuestion {...props}>Ask Question</AskQuestion>
          </div>
        </div>
      </Layout>
    );
  }
}

const mapStateToProps = state => ({
  questions: state.question.questions,
  questionsLoader: state.question.questionsLoader
});

const mapDispatchToProps = dispatch => {
  return {
    getQuestions: (search, message) => dispatch(getQuestions(search, message))
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Questions);
