import React, { useEffect } from "react";
import { Form, Icon, Input, Button, Checkbox } from "antd";
import { loginUser } from "../../redux/index";
import { connect } from "react-redux";
import { message } from "antd";
import  Link from "next/link";
import cookie from "react-cookies";
import  Router from 'next/router';

function LoginForm(props) {
  useEffect(()=>{
    if(cookie.load('token')){
      return Router.push('/','/', { shallow: true })
    }
  },[])
  const handleSubmit = e => {
    e.preventDefault();

    props.form.validateFields((err, values) => {
      if (!err) {
        console.log("Received values of form: ", values);
        props.loginUser(values, props.history, message);
      }
    });
  };
  const { getFieldDecorator } = props.form;
  return (
    <div className="register__form-container">
      <Form onSubmit={handleSubmit} className="login-form">
        <Form.Item>
          {getFieldDecorator("email", {
            rules: [{ required: true, message: "Please input your username!" }]
          })(
            <Input
              prefix={<Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />}
              placeholder="Username"
            />
          )}
        </Form.Item>
        <Form.Item>
          {getFieldDecorator("password", {
            rules: [{ required: true, message: "Please input your Password!" }]
          })(
            <Input
              prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
              type="password"
              placeholder="Password"
            />
          )}
        </Form.Item>
        <Form.Item>
          <Button
            type="primary"
            loading={props.loginLoader}
            htmlType="submit"
            className="login-form-button"
          >
            Log in
          </Button>
          <br />
          Or <Link href="/register"><a>register now!</a></Link>
        </Form.Item>
      </Form>
    </div>
  );
}

const WrappedLoginForm = Form.create({ name: "login_form" })(LoginForm);
const mapStateToProps = state => ({
  loginLoader: state.user.loginLoader
});

const mapDispatchToProps = dispatch => {
  return {
    loginUser: (data, history, message) =>
      loginUser(data, history, message)(dispatch)
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(WrappedLoginForm);
